/*Table structure for table `tangshi` */

DROP TABLE IF EXISTS `tangshi`;

CREATE TABLE `tangshi` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `sha256` char(64) NOT NULL,
  `dynasty` varchar(10) NOT NULL,
  `title` varchar(100) NOT NULL,
  `author` varchar(20) NOT NULL,
  `content` text NOT NULL,
  `words` text NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `sha256` (`sha256`)
) ENGINE=InnoDB AUTO_INCREMENT=321 DEFAULT CHARSET=utf8mb4;

/*Data for the table `tangshi` */

insert  into `tangshi`(`id`,`sha256`,`dynasty`,`title`,`author`,`content`,`words`) values (1,'44f05bb1e88aa1191f1b2cc2b2fed765d55e1538861e01c6aa54e78d9c06a243','唐代','问刘十九','白居易','绿蚁新醅酒，红泥小火炉。晚来天欲雪，能饮一杯无？','刘十九,绿蚁,醅酒,火炉,一杯'),(2,'a2770c066b548d40f51b6c2dd1659f9320b933a28ab0be0034ab587645f7983b','唐代','崔九欲往南山马上口号与别 / 留别王维 / 送崔九','裴迪','归山深浅去，须尽丘壑美。莫学武陵人，暂游桃源里。','崔九,南山,马上,口号,别王维,崔九,归山,深浅,丘壑,莫学武陵人,桃源'),(3,'2b9fa67d2b6a0ac376744aa8f551e2b206286bae0723ca36a97a2f663529c187','唐代','送别 / 山中送别','王维','山中相送罢，日暮掩柴扉。春草明年绿，王孙归不归？(明年 一作：年年)','送别,山中,送别,日暮掩柴扉,春草,明年,王孙,明年,年年'),(4,'f278e6c59f3bcac082b29b8c013c0c0739237ceb50ff5b9fb7359d584e18cea6','唐代','芙蓉楼送辛渐','王昌龄','寒雨连江夜入吴，平明送客楚山孤。洛阳亲友如相问，一片冰心在玉壶。','芙蓉楼,辛渐,寒雨连,江夜,平明,送客,楚山,洛阳,亲友,一片,冰心,玉壶'),(5,'a00cc5454f87a3972fc2b67e51da1354fdd02ccbffc7905a1db31abc4712ffcc','唐代','乐游原 / 登乐游原','李商隐','向晚意不适，驱车登古原。夕阳无限好，只是近黄昏。','乐游,登乐,游原,晚意,不适,驱车,古原,夕阳,无限,只是,黄昏'),(6,'072152ac898ce6bb2f01459a09e6208a83d0d0d38733745c619f053fcb74f596','唐代','春怨 / 伊州歌','金昌绪','打起黄莺儿，莫教枝上啼。啼时惊妾梦，不得到辽西。','春怨,打起,黄莺儿,啼时,惊妾梦,得到,辽西'),(7,'822866ed4c12babc26e4b02f6cc77c3e4dc0ba6ba000768c2016fa2cc4ff8244','唐代','渡汉江','宋之问','岭外音书断，经冬复历春。近乡情更怯，不敢问来人。','汉江,岭外,音书,冬复,历春,乡情,来人'),(8,'71c3260594f2aec548cbe45c18713eaee5c627b3ce667e04c923ec1933504579','唐代','行宫','元稹','寥落古行宫，宫花寂寞红。白头宫女在，闲坐说玄宗。','行宫,寥落古行宫,宫花,寂寞,白头宫女,闲坐,玄宗'),(9,'4daae4fcfba261225dacefa7ba0decc91c12b0828ac91e93d49bcf9758ef7463','唐代','送方外上人 / 送上人','刘长卿','孤云将野鹤，岂向人间住。莫买沃洲山，时人已知处。','送方,孤云,野鹤,人间,莫买沃洲山,时人,知处'),(10,'caa8b96ab9e6e985907d5c6c9aa44eef20a544703897c07eb639393166fd2622','唐代','宿建德江','孟浩然','移舟泊烟渚，日暮客愁新。野旷天低树，江清月近人。','宿建德,舟泊,烟渚,客愁,野旷天低树,江清月近人'),(11,'41767a2deae63790d793c495b6e49401006fb295648d627784b4b56c10c9064c','唐代','听弹琴','刘长卿','泠泠七弦上，静听松风寒。(七弦 一作：七丝)古调虽自爱，今人多不弹。','弹琴,泠泠,静听,风寒,七丝,自爱,今人'),(12,'8b6c859096d6ebea3af6bc92f69cc97977b15918cb6e2f6b0849aff431b4a75c','唐代','新嫁娘词','王建','三日入厨下，洗手作羹汤。未谙姑食性，先遣小姑尝。','新嫁娘,三日,洗手,羹汤,未谙,姑食性,先遣,小姑'),(13,'56eafd21bc8702de89a5953c6f0cc948709368373b9c696503d330e28663a7cb','唐代','相思','王维','红豆生南国，春来发几枝。愿君多采撷，此物最相思。','相思,红豆生,南国,采撷,相思'),(14,'8e2908274d15a18e59860ee5d868cbe33fe8e47ea6b5f5317f1f46ad23158f1d','唐代','宫词二首·其一','张祜','故国三千里，深宫二十年。一声何满子，双泪落君前。','宫词,二首,其一,故国,三千里,深宫,二十年,一声,满子'),(15,'00f1af02b7194d0e9fb5db38115881f59bcfc0b636d7b5eb38e6dfe30540588c','唐代','春晓','孟浩然','春眠不觉晓，处处闻啼鸟。夜来风雨声，花落知多少。','春晓,春眠,觉晓,处处,啼鸟,风雨,多少'),(16,'6cbd7ce5bc261a2ea2fb02140a980a10e1ed7c2e26c9c9f922f263f09a87bd15','唐代','秋夜寄邱员外 / 秋夜寄丘二十二员外','韦应物','怀君属秋夜，散步咏凉天。山空松子落，幽人应未眠。','秋夜,邱员外,秋夜,寄丘,二十二员,怀君,秋夜,散步,山空,松子,幽人'),(17,'21c0fedcf1b5915be8ce1512158f8c10ed5d3caf8e0aabef4c01866c909829fb','唐代','静夜思','李白','床前明月光，疑是地上霜。举头望明月，低头思故乡。','静夜思,明月,地上,举头,明月,低头,故乡'),(18,'d12cff3d5dc6aa4fbfc88079bb17d44a0dac43284a44f1e0dc438cebd9e90623','唐代','江雪','柳宗元','千山鸟飞绝，万径人踪灭。孤舟蓑笠翁，独钓寒江雪。','江雪,千山鸟飞绝,踪灭,孤舟蓑笠翁,寒江'),(19,'8de2cdc823114a1460c6249e46b7d0f325644964981bfffa392da56816551f14','唐代','竹里馆','王维','独坐幽篁里，弹琴复长啸。深林人不知，明月来相照。','竹里馆,幽篁,弹琴,长啸,深林人不知,明月,相照'),(20,'629d926d9dbab07f072cbb0b02e28b09af970b6e8dcba077a090211decc304f8','唐代','怨情','李白','美人卷珠帘，深坐颦蛾眉。(颦 一作：蹙)但见泪痕湿，不知心恨谁。','怨情,美人,珠帘,蛾眉,不知,心恨'),(21,'c9066984a41ed2ed6111927467fd2e2f3e208fa5e53d5498565daa8b6df76899','唐代','终南望余雪 / 终南望残雪','祖咏','终南阴岭秀，积雪浮云端。林表明霁色，城中增暮寒。','终南,终南,残雪,终南阴岭秀,积雪,云端,表明,霁色,暮寒'),(22,'e2690d782ac8a9666edbbf9aedb23a666ee27ca614692bf2af466db5ca69bbd7','唐代','听筝 / 鸣筝','李端','鸣筝金粟柱，素手玉房前。欲得周郎顾，时时误拂弦。','鸣筝,鸣筝金,粟柱,素手,玉房,周郎顾,时时'),(23,'1a8c2f2d8f655db1f072dc3840ef980f42962f9208b8bf674e3d2da786e9795a','唐代','寻隐者不遇 / 孙革访羊尊师诗','贾岛','松下问童子，言师采药去。只在此山中，云深不知处。','寻隐者,孙革访,羊尊师诗,松下问,童子,言师,采药,云深,不知'),(24,'d9c16c0ffbbb1488a08c00fb481eda7918321d3c9bd72a9a7e3eac8fb89d78fc','唐代','哥舒歌','西鄙人','北斗七星高，哥舒夜带刀。至今窥牧马，不敢过临洮。','哥舒歌,北斗七星高,舒夜,至今,牧马,临洮'),(25,'19f46e03a54e92a1920f43da33405234112eb8a733634e4eba6d27c824ec27d7','唐代','杂诗三首·其二','王维','君自故乡来，应知故乡事。来日绮窗前，寒梅著花未？','杂诗,三首·其二,故乡,故乡,来日,窗前,寒梅,著花'),(26,'32a14e9110787c22e0b2e29acc107fb6c47c6cf282d034d8e52e69debdcebe53','唐代','鹿柴','王维','空山不见人，但闻人语响。返景入深林，复照青苔上。','鹿柴,空山,不见,返景,深林,复照,青苔'),(27,'f4d627af76e195f08f3ae0d7d6116bd61384486a68864190924df2d3a92a3f14','唐代','登鹳雀楼','王之涣','白日依山尽，黄河入海流。欲穷千里目，更上一层楼。','登鹳雀楼,白日,黄河,千里,更上一层楼'),(28,'f44f62c275e7cc0abf0ac4378fcdd40af2f7b93b103c6499dcfc1ab472da897d','唐代','八阵图','杜甫','功盖三分国，名成八阵图。（名成 一作：名高）江流石不转，遗恨失吞吴。','阵图,功盖,三分,名成,八阵,名成,名高,江流石,遗恨,吞吴'),(29,'53369fccd49b2b52fa73d3a9847395b4f126377f6019bfbdf9b72d5cfc9a7856','唐代','玉台体','权德舆','昨夜裙带解，今朝蟢子飞。铅华不可弃，莫是藁砧归。','台体,昨夜,裙带解,今朝,蟢子,铅华,不可弃,藁砧'),(30,'bb5fbd00aacb2f11cf5e922a3dcacd7d33722db19bd60dd7558ba9938980a721','唐代','送灵澈上人','刘长卿','苍苍竹林寺，杳杳钟声晚。荷笠带斜阳，青山独归远。(斜阳 一作：夕阳)','灵澈,上人,苍苍竹林寺,杳杳,钟声,荷笠,斜阳,青山,斜阳,夕阳'),(31,'2ee3160b72893f521dc0e19da87def8ff7730efeb90151b8325c7dde10e1f3b7','唐代','征人怨 / 征怨','柳中庸','岁岁金河复玉关，朝朝马策与刀环。 三春白雪归青冢，万里黄河绕黑山。','征人,征怨,岁岁,金河,玉关,马策,刀环,白雪,青冢,万里,黄河,黑山'),(32,'f6cf4b8c60a1523948c14fb20d07c04125949db4f8a6588e57c4791499ede00b','唐代','金谷园','杜牧','繁华事散逐香尘，流水无情草自春。日暮东风怨啼鸟，落花犹似坠楼人。','金谷园,繁华,散逐,香尘,流水,日暮东风怨啼鸟,落花'),(33,'e2b42a80974bb796858c1ad6c4adb45af2926ab1ff7dbc69b12cfb6b9a5eb47e','唐代','金陵图','韦庄','谁谓伤心画不成，画人心逐世人情。 君看六幅南朝事，老木寒云满故城。','金陵图,伤心,人心,逐世,人情,六幅,南朝,老木,寒云满,故城'),(34,'d75f01e79bda886a0d6d22c85dea74150f2ea5a884080424d8fb0d913f87aed5','唐代','瑶池','李商隐','瑶池阿母绮窗开，黄竹歌声动地哀。 八骏日行三万里，穆王何事不重来。','瑶池,瑶池,阿母,黄竹歌,声动,骏日,万里,穆王,何事'),(35,'58707d822566127678e9484988e2ea2419850a8ac7b53644d83c17687a8532b0','唐代','隋宫','李商隐','乘兴南游不戒严，九重谁省谏书函。 春风举国裁宫锦，半作障泥半作帆。','隋宫,乘兴,南游,戒严,九重,书函,春风,举国,宫锦'),(36,'73944e4c15645da578d0a592a56ac4313641b3fc6ce47f2fe1d94a6676fa0288','唐代','后宫词','白居易','泪湿罗巾梦不成，夜深前殿按歌声。 红颜未老恩先断，斜倚薰笼坐到明。','后宫,罗巾,夜深,歌声,红颜,老恩'),(37,'9fb47a4a584db6f4ed272e3321bb4d8ad473df5b7c9b45f660d101b1fd4f6d96','唐代','黄鹤楼送孟浩然之广陵','李白','故人西辞黄鹤楼，烟花三月下扬州。孤帆远影碧空尽，唯见长江天际流。 (唯 通：惟)','黄鹤楼,孟浩然之广陵,故人,西辞,黄鹤楼,烟花,三月,扬州,孤帆远影碧空尽,长江,天际'),(38,'068f2bef0e62dbf65ce271c8bb0db32be006bbd7622cf75cacb083f84c3fcfdf','唐代','泊秦淮','杜牧','烟笼寒水月笼沙，夜泊秦淮近酒家。商女不知亡国恨，隔江犹唱后庭花。','秦淮,烟笼,夜泊,秦淮,酒家,商女,不知,亡国,隔江'),(39,'eb9dd28ea589410a00b455f57b954270c6a3926a423d6146ea71c12a13380a28','唐代','早发白帝城 / 白帝下江陵','李白','朝辞白帝彩云间，千里江陵一日还。两岸猿声啼不住，轻舟已过万重山。','白帝,白帝,江陵,朝辞,白帝,彩云,千里,江陵,一日,两岸,猿声,轻舟,万重'),(40,'36a30c782253a4d70e9c